#Exit incase of error
set -e

# PAI includes

PAI_SCRIPT_ID=

PAI=/var/PAI

. $PAI/System/pai.sh

# PAI functions

pai_intro() {
pai_log_sep
pai_log 'starting pai-install-bot...'
}

pai_install_bot() {
echo ""
echo -e 'Bot git clone:'
git clone https://github.com/PAI-Tech/PAI-BOT-JS.git
cd PAI-BOT-JS
echo ""
echo -e 'npm login:'
npm login
echo ""
npm install --no-save
echo ""
node PAI.js
}

pai_end() {
pai_log 'done :)'
}

# PAI main flow

pai_intro
pai_install_bot
pai_end
